export const DELETE = 'DELETE';

export const isDeleteItem = ({ id }, { client }) => {
  console.log(client);
  return client.filter((el) => el.id !== id);
};

export default function deleteClient(id) {
  return {
    type: DELETE,
    payload: {
      id,
    },
  };
}
