import React from 'react';
import { Link } from 'react-router-dom';

class ClientRegister extends React.Component {
  constructor() {
    super();
    this.state = {
      listFake: [2],
    };
  }

  render() {
    const { listFake } = this.state;
    return (
      <div>
        {
          listFake.length
            ? <h1>Clientes cadastros</h1> : <h1>Nenhum cliente cadastrado</h1>
        }
        <Link to="/cadastrar">
          <button type="button">Cadastrar</button>
        </Link>
      </div>
    );
  }
}

export default ClientRegister;
