import { REGISTER } from '../actions/register';

const initialState = {
  client: [],
};

function registerReducer(state = initialState, action) {
  console.log('states');
  console.log(state);
  console.log('ACTIONS');
  console.log(action);
  switch (action.type) {
  case REGISTER:
    return {
      ...state,
      client: state.client.concat(action.payload.client),
    };
  default:
    return state;
  }
}

export default registerReducer;
