import { REGISTER } from '../actions/register';

const initialState = {
  payload: {
    client: [],
  },
};

function registerReducer(state = initialState, action) {
  console.log('states');
  console.log(state.payload.action);
  // console.log('ACTIONS');
  // console.log(action);
  switch (action.type) {
  case REGISTER:
    return state.payload.client.concat(action.payload);
  default:
    return state;
  }
}

export default registerReducer;
