export const DELETE = 'DELETE';

export const isDeleteItem = ({ client }, { id }) => {
  console.log(client);
  console.log(id);
  const result = client.filter((el) => el.id !== id);
  return result;
};

export default function deleteClient(id) {
  return {
    type: DELETE,
    payload: {
      id,
    },
  };
}
