import React from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';

class ClientRegister extends React.Component {
  constructor() {
    super();
    this.state = {
      listFake: [],
    };
  }

  render() {
    const { listFake } = this.state;
    return (
      <div>
        {
          listFake.length
            ? <h1>Clientes cadastros</h1> : <h1>Nenhum cliente cadastrado</h1>
        }
        <Link to="/cadastrar">
          <button type="button">Cadastrar</button>
        </Link>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  listClient: state.payload,
});

export default connect(mapStateToProps, null)(ClientRegister);
