export const DELETE = 'DELETE';

export const isDeleteItem = ({ client }, { id }) => {
  console.log(client);
  console.log(id);
  const result = client.filter((el) => {
    console.log('id');
    console.log(id);
    console.log('id client');
    console.log(el.id);
    return el.id !== id;
  });
  console.log(result);
  return result;
};

export default function deleteClient(id) {
  return {
    type: DELETE,
    payload: {
      id,
    },
  };
}
